import { React, useEffect, useState } from "react";
import axios from "axios";

const Details = ({ id }) => {
  const [movieDetails, setMovieDetails] = useState({});
  id = "cw2488496";
  const url = "https://challenge.lexicondigital.com.au/api/cinemaworld/movie/";

  useEffect(() => {
    const fetchDetails = () => {
      axios
        .get(url + id)
        .then((response) => {
          console.log(response.data);
          setMovieDetails(response.data);
        })
        .catch((error) => {
          console.log("Error fetching movie details..");
          console.log(error);
        });
    };
    fetchDetails();
  });
  return (
    <div>
      <center>
        <h1 style={{ fontSize: 45, color: "grey" }}>CinemaWorld Movies</h1>
      </center>
    </div>
  );
};

export default Details;
