import { React, useEffect, useState } from "react";
import axios from "axios";

const MovieID = ({ id }) => {
  const [movieDetails, setMovieDetails] = useState({});
  const url = "https://challenge.lexicondigital.com.au/api/cinemaworld/movie/";

  useEffect(() => {
    const fetchDetails = () => {
      axios
        .get(url + id[0])
        .then((response) => {
          console.log(response.data);
          setMovieDetails(response.data);
        })
        .catch((error) => {
          console.log("Error fetching movie details..");
          console.log(error);
        });
    };
    fetchDetails();
  });
  return (
    <div>
      <center>
        <h1 style={{ fontSize: 45, color: "grey" }}>ID Numbers</h1>
      </center>
      {movieDetails.map((number) => (
        <div class="card">
          <div class="card-body">
            <h6 class="card-subtitle mb-2 text-muted">{number}</h6>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MovieID;
