import React, { useState, useEffect } from "react";
import "./App.css";

function Item({ match }) {
  useEffect(() => {
    fetchItem();
    //console.log(match.params.id);
  }, []);

  const [item, setItem] = useState({
    images: {},
  });

  const fetchItem = async () => {
    const fetchItems = await fetch(
      "https://challenge.lexicondigital.com.au/api/cinemaworld/movie/" +
        match.params.id,
      {
        headers: {
          "x-api-key": "Yr2636E6BTD3UCdleMkf7UEdqKnd9n361TQL9An7",
        },
      }
    );
    const item = await fetchItems.json();
    setItem(item);
    console.log(item);
  };

  return (
    <div>
      <ul>
        <img src={item.Poster} alt="" />
        <h5>{item.Tile}</h5>
        <h5>{item.Year}</h5>
        <h5>{item.Genre}</h5>
        <h5>{item.Rated}</h5>
        <h5>{item.Released}</h5>
        <h5>{item.Language}</h5>
      </ul>
    </div>
  );
}

export default Item;
