      "https://challenge.lexicondigital.com.au/api/cinemaworld/movie/" +
        match.params.id,
      { headers: { "x-api-key": "Yr2636E6BTD3UCdleMkf7UEdqKnd9n361TQL9An7" } }
    );
    //console.log(data);
    const items = await data.json();
