import React, {Component} from 'react';
import Contacts from './Cinemaworld.js';
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect
} from "react-router-dom";

class App extends Component {
    render() {
        return (
            <Router>
                <Contacts cinemaWorld={this.state.cinemaWorld} />
            </Router>
        )
    }

    state = {
        cinemaWorld: [],
        filmworld: [],
        IDs: []
    };

    componentDidMount() {
        fetch('https://challenge.lexicondigital.com.au/api/cinemaworld/movies/', {headers: {'x-api-key': 'Yr2636E6BTD3UCdleMkf7UEdqKnd9n361TQL9An7' }})
            .then(res => res.json())
            .then((data) => {

                this.setState({ cinemaWorld: data.Movies })
            })
            fetch('https://challenge.lexicondigital.com.au/api/filmworld/movies/', {headers: {'x-api-key': 'Yr2636E6BTD3UCdleMkf7UEdqKnd9n361TQL9An7' }})
            .then(res => res.json())
            .then((data) => {
                let tmpArray = []
                for (var i = 0; i < data.Movies.length; i++){
                  tmpArray.push(data.Movies[i])
                }
                this.setState({ IDs: tmpArray})

            })

            .catch(console.log)
    }
}

export default App;
