import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios'

const api = axios.create({
  baseURL: 'https://challenge.lexicondigital.com.au/api/cinemaworld/movies',
  header : {
    'x-api-key' : "Yr2636E6BTD3UCdleMkf7UEdqKnd9n361TQL9An",
    'Access-Control-Allow-Origin': '*',
  },
})

class App extends Component {

  constructor(){
    super();
    api.get('https://challenge.lexicondigital.com.au/api/cinemaworld/movies', {
      headers: {
        'Access-Control-Allow-Origin': '*',
      }
    }).then(res => {
      console.log(res.data)
    })
  }

  render(){
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
}

export default App;
